import React from 'react';

const TagList = ({ tags }) => {
    return (
        <div>
            <ul className="tag-list">
                { tags.map(tag => (
                    <li key={tag} className="tag-default tag-pill tag-outline">
                        { tag }
                    </li>
                )) }
            </ul>
        </div>
    );
};

export default TagList;